mapper.select:
SELECT B.* FROM (SELECT rownum as rnum, A.* FROM (SELECT LOCATION_ID, LOCATION, LCENTERX, LCENTERY, LOCATION_IMAGE, LOCATION_TITLE
FROM LOCATION ORDER BY ${order}) A 

WHERE ROWNUM <= ${limit+4}) B WHERE B.rnum > ${limit}
